// SPDX-License-Identifier: MIT
pragma solidity ^0.6.2;

// import '@openzeppelin/contracts/proxy/Initializable.sol';
// import '@openzeppelin/upgrades/contracts/Initializable.sol';

contract Auction {
  // address public clone;
  // string public name;
  uint public amount;
  address public tokenContract;
  uint startDate;
  uint endDate;
  uint public sellerDeposit;
  uint public buyerDeposit;

  constructor(uint _amount, address _tokenContract, uint _startDate, uint _endDate) public {
    // name = _name;
    amount = _amount;
    tokenContract = _tokenContract;
    startDate = _startDate;
    endDate = _endDate;
  }

  function setAmount(uint _amount) public {
    amount = _amount;
  }

  function getAmount() public view returns (uint) {
    return amount;
  }

  function getTokenContract() public view returns (address) {
    return tokenContract;
  }

  function getStartDate() public view returns (uint) {
    return startDate;
  }

  function getEndDate() public view returns (uint) {
    return endDate;
  }
}
