// SPDX-License-Identifier: MIT
pragma solidity ^0.6.2;

import './Auction.sol';
// import '@openzeppelin/contracts/access/Ownable.sol';

contract AuctionFactory {
  Auction[] public auctionAddresses;
  address private owner;
  address public wtf;

  event AuctionCreated(Auction auction);

  constructor() public {
    owner = msg.sender;
    wtf = owner;
  }

  function getAddresses() public view returns (Auction[] memory) {
    return auctionAddresses;
  }

  function createAuction(uint amount, address tokenContract, uint startDate, uint endDate) public returns (address) {
    Auction auction = new Auction(amount, tokenContract, startDate, endDate);
    auctionAddresses.push(auction);
    emit AuctionCreated(auction);
  }
}
